mapper.update:
UPDATE tbl_user
SET weight = #{weight} , purpose_id = #{purposeId} , active_point = #{activePoint}
WHERE id = #{userId}

mapper.select:
SELECT * FROM tbl_food WHERE id IN (
SELECT food_id FROM tbl_fm_with_food 
WHERE date = #{date} AND fm_id = (SELECT id FROM tbl_food_management WHERE user_id = 1 AND DATE =(SELECT MAX(date) FROM tbl_food_management)));
