mapper.update:
UPDATE tbl_user
SET nickname = #{nickname}, password = #{password}, active_point = #{activePoint}, purpose_id = #{purposeId}
WHERE id = #{id};

      UPDATE tbl_user
			SET isWithdraw = 1, withdraw_reason = #{withdraw_reason}
			WHERE id = #{id};

UPDATE tbl_user
SET password = #{password}
WHERE id = #{id};

UPDATE tbl_user
SET isWithdraw = 1, withdraw_reason = #{withdrawReason}
WHERE id = #{id};

mapper.select:
SELECT user.id, user.name, user.email, user.nickname, user.purpose_id, user.active_point, user.weight, purpose.purpose
FROM tbl_user AS user, tbl_purpose AS purpose
WHERE user.email = #{email} AND user.password = #{password} AND user.isWithdraw = 0 AND user.purpose_id = purpose.id;

SELECT name, email, password, id, nickname, weight, active_point
FROM tbl_user
WHERE email = #{email} AND isWithdraw = 0;
