mapper.update:
UPDATE tbl_user
SET nickname = #{nickname}, password = #{password} , active_point = #{active_point}
WHERE id = #{id};

      UPDATE tbl_user
			SET isWithdraw = 1, withdraw_reason = #{withdraw_reason}
			WHERE id = #{id};

UPDATE tbl_user
SET password = #{password}
WHERE isWithdraw = 0 AND email = #{email};

UPDATE tbl_user
SET isWithdraw = 1, withdraw_reason = #{withdraw_reason}
WHERE id = #{id};

mapper.select:
SELECT name, email, nickname, purpose_id, active_point, weight
FROM tbl_user
WHERE email = #{email} and password = #{password}

SELECT name, email, password, id, nickname, weight , active_point
FROM tbl_user
WHERE email = #{email}
